/**
 * Finds the number of sprites defined above. The result is cached so the
 * full function only runs once.
 *
 * @param 
 * @return 
 */
size_t sprite_count()
{
    static size_t i=0;
    if (i == 0) {
        while(dwr_sprite_names[i] != NULL) {
            i++;
        }
        printf("Found %lu sprites.\n", i);
    }
    return i;
}

