/**
 * Changes the palette of a character in the game.
 *
 * @param rom The randomized rom
 * @param npc the character to change the palette of
 * @palette which palette to use. Valid values are 0-3.
 */
static void switch_npc_palette(dw_rom *rom, NPC npc, uint8_t palette)
{
    int i,j;

    for (i=0; i < 0x400; i+=0x100) {
        for (j=0; j < 0x10; j+=2) {
            rom->content[0x1335 + (npc * 0x10) + i + j] &= UINT8_C(0xfc);
            rom->content[0x1335 + (npc * 0x10) + i + j] |= palette;
        }
    }
}

