/**
 * Changes the palette of a character in the game.
 *
 * @param rom The randomized rom
 * @param npc the character to change the palette of
 * @palette which palette to use. Valid values are 0-3.
 */
static void set_npc_palette(dw_rom *rom, NPC npc, uint8_t palette)
{
    int i,j;

    for (i=0; i < 0x400; i+=0x100) {
        for (j=0; j < 0x10; j+=2) {
            rom->content[0x1335 + (npc * 0x10) + i + j] &= UINT8_C(0xfc);
            rom->content[0x1335 + (npc * 0x10) + i + j] |= palette;
        }
    }
}

/**
 * Sets the colors for the given player/npc palette
 *
 * @param rom The rom struct
 * @param palette The palette number. Should be 0-3.
 *  @param skin The skin color for the sprite.
 *  @param light_color The light color for the sprite.
 *  @param dark_color The dark color for the sprite.
 */
static void set_palette_colors(dw_rom *rom, uint8_t palette,
        uint8_t skin, uint8_t light_color, uint8_t dark_color)
{
    if (palette > 3) {
        printf("Improper palette number of %d, ignoring.\n", palette);
        return;
    }
    vpatch(rom, 0x01a8b+(palette*3), 3, skin, light_color, dark_color);
}

